// This file handles single entries, but only exists for the sake of child theme forward compatibility.
add_filter( 'genesis_pre_get_option_site_layout', 'content_sidebar_layout_single_posts' );
function content_sidebar_layout_single_posts( $opt ) {
    $opt = 'content-sidebar'; 
    return $opt;
}



remove_action('genesis_loop', 'genesis_do_loop');

add_action('genesis_loop', 'gt_custom_loop');
function gt_custom_loop() {

	if (have_posts()) :
		while (have_posts()) : the_post();
			?>
			<header class="entry-header">
				<h1 class="entry-title" itemprop="headline"><?php echo get_the_title();?></h1>
			</header>
			<div class="img-consejos">
			<?php
				the_post_thumbnail( 'full' );
			?>
			</div>

			<div class="content-consejos">

			<p ><span class="entry-date"><?php echo get_the_date('j'). ' de '.get_the_date('F'). ' de '. get_the_date('Y'); ?></span> | Consejos</p>
			<?php
				the_content();


			?>
			<div class="addthis_toolbox addthis_default_style addthis_32x32_style share">
			  <a class="addthis_counter_facebook"></a>
			  <a class="addthis_counter_twitter"></a>
			  <a class="addthis_counter_google"></a>
			  <a class="addthis_counter_linkedin"></a>
			</div>
			

